import React from "react";
import { useState } from "react";
import { getAPIOptions, GEO_API_URL} from './api';
const Search = ({ onSearchChange }) => {
  const [search, setSearch] = useState('');

  const searchandler = (searchData) => {
    setSearch(searchData);
    onSearchChange(searchData)
  };

  const loadOptions = (inputValue) => {
    fetch(`${GEO_API_URL}/cities?minPopulation=1000000&namePrefix=${inputValue}`, getAPIOptions)
	.then(response => response.json())
	.then(response => console.log(response))
	.catch(err => console.error(err));
  }
  return (
    <div className="container">
      <label htmlFor="">Enter a city name : </label>
      <input
        style={{ borderRadius: "2px" }}
        type="text"
        placeholder="Search for a city"
        debouncetimeout={600}
        value={search}
        onChange={searchandler}
        loadOptions = {loadOptions}
      />
    </div>
  );
};

export default Search;
